import './App.css'
import { useState } from 'react';

function App() {
  const initialTodos = [
    {
      id: 1, 
      name: "Pay 1",
      status: 1 // 1 for unchecked, 0 for checked
    },
    {
      id: 2, 
      name: "Pay 2",
      status: 1
    },
    {
      id: 3, 
      name: "Pay 3",
      status: 1
    }
  ];

  const [todoList, setTodoList] = useState(initialTodos);
  const [inputValue, setInputValue] = useState("");

  const addTodo = () => {
    if (inputValue.trim()) {
      const newTodo = { id: todoList.length + 1, name: inputValue, status: 1 };
      setTodoList([...todoList, newTodo]);
      setInputValue("");  // Clear input field
    }
  };

  const toggleChecked = (id) => {
    setTodoList(
      todoList.map(todo =>
        todo.id === id ? { ...todo, status: todo.status === 1 ? 0 : 1 } : todo
      )
    );
  };

  return (
    <div className="container">
      <div id="myDIV" className="header">
        <h2 style={{ margin: "5px" }}>My To Do List</h2>
        <input
          type="text"
          id="myInput"
          placeholder="Title..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <span onClick={addTodo} className="addBtn">Add</span>
      </div>

      <ul id="myUL">
        {todoList.map((todo) => (
          <li
            key={todo.id}
            className={todo.status === 0 ? "checked" : ""}
            onClick={() => toggleChecked(todo.id)} // Toggle checked status on click
          >
            {todo.name}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
