import './App.css'
function App() {
  const todoList = [
    {
      id: 1, 
      name: "Pay 1",
      status: 1
    },
    {
      id: 2, 
      name: "Pay 2",
      status: 1
    },
    {
      id: 3, 
      name: "Pay 3",
      status: 1
    }
  ]

  const addTodo = () => {
    let input = document.getElementById('myInput')
    let li = document.createElement("li")
    li.innerHTML = input.value
    let ul = document.getElementById("myUL")
    ul.appendChild(li)
    input.value = ""
  }

  const checkEd = (id) => {
    todoList.map((e)=> 
      e.id === id ? document.getElementById(e.id).className = "checked": ""

  )
  }


  return (
    <div class="container">
      <div id="myDIV" class="header">
        <h2 style={{margin:"5px"}}>My To Do List</h2>
        <input type="text" id="myInput" placeholder="Title..."/>
          <span onClick={addTodo} class="addBtn">Add</span>
      </div>

      <ul id="myUL">
        {
          todoList.map((e)=> 
            <li onClick={checkEd(e.id)} key={e.id}>{e.name}</li>
          )
        }
      </ul>

    </div>
  );
}

export default App;
