import './App.css'
import { useState } from "react";
import data from './data.json'
function App() {
  const [checkStatus, setCheckStatus] = useState();
  const [todoList, setTodoList] = useState(data);
  const checked = () => {
    return setCheckStatus("checked")
  }
  const addTodo = ()=>{
    const valueInput = document.getElementById("myInput").value;
    const temp = todoList
    setTodoList((temp) => {
      temp.push({name: valueInput, status: false})
    })
  }
  return (
    <div className="container">
      <div id="myDIV" className="header">
        <h2 style={{ margin: "5px" }}>My To Do List</h2>
        <input type="text" id="myInput" placeholder="Title..." />
        <span onClick={addTodo} className="addBtn">Add</span>
      </div>
      <ul id="myUL">
        {
          data.map((e, index) =>
            <li onClick={() => checked()} className={checkStatus} key={index}>{e.name}</li>
          )
        }
      </ul>
    </div>
  );
}

export default App;
