import './App.css';
import { useState, useEffect } from 'react';

function App() {
  const [todoList, setTodoList] = useState([]);
  const [inputValue, setInputValue] = useState("");

  useEffect(() => {
    // Fetch data from the JSON file
    fetch('/todos.json')
      .then(response => response.json())
      .then(data => setTodoList(data))
      .catch(error => console.error('Error fetching the todo list:', error));
  }, []);

  const addTodo = () => {
    if (inputValue.trim()) {
      const newTodo = { id: todoList.length + 1, name: inputValue, status: 1 };
      setTodoList([...todoList, newTodo]);
      setInputValue("");  // Clear input field
    }
  };

  const toggleChecked = (id) => {
    setTodoList(
      todoList.map(todo =>
        todo.id === id ? { ...todo, status: todo.status === 1 ? 0 : 1 } : todo
      )
    );
  };

  const removeTodo = (id) => {
    setTodoList(todoList.filter(todo => todo.id !== id));
  };

  return (
    <div className="container">
      <div id="myDIV" className="header">
        <h2 style={{ margin: "5px" }}>My To Do List</h2>
        <input
          type="text"
          id="myInput"
          placeholder="Title..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <span onClick={addTodo} className="addBtn">Add</span>
      </div>

      <ul id="myUL">
        {todoList.map((todo) => (
          <li
            key={todo.id}
            className={todo.status === 0 ? "checked" : ""}
          >
            <span onClick={() => toggleChecked(todo.id)}>{todo.name}</span>
            <button className="removeBtn" onClick={() => removeTodo(todo.id)}>Remove</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
